import { useState } from 'react'

const useForm = (callback, validate) => {
    const [values, setValues] = useState({
        name: "",
        email: "",
        body: "",
        subject: ""
    });
    const [errors, setErrors] = useState();



    const onChange = e => {
        const {name, value} = e.target;
        setValues({
            ...values,
            [name]: value
        })
    }
}
