import React from 'react'
import styles from './styles.module.css'

export const Avatar = ({initials, src, size}) => {
    if(!size) {
        size = 'auto'
    } else {
        size = '' + size + 'px'
    }
    return (
        <div>
            {initials && (
                <span>{initials} </span> 
            )}
            {!initials && (
                <img src={src} style={{height: `${size}`,width: `${size}`}} className={styles.avatar}/>
            )}

        </div>
    )
}
