import React from 'react'
import { TextField, Container, Grid, Button } from '@material-ui/core';
import { withRouter } from 'react-router';
import { sendMessage } from '../../Data/Api/MessageApi';
import { useForm } from "react-hook-form";

function Contact() {

    const { register, handleSubmit, watch, errors } = useForm();

    const onSubmit = async (e) => {
        e.preventDefault();
        const { name, email, subject, body } = e.target;
        const message = {
            name: name.value,
            body: body.value,
            ...(!!subject.value && {subject: subject.value}),
            ...(!!email.value && {email: email.value})
        }; 

        // sendMessage(message);
    }

    return (
        <div>
            <Container maxWidth='sm' >
                <h1>Contact</h1>
                <form novalidate onSubmit={onSubmit}>
                    <Grid container spacing={2}>
                        <Grid item xs={12}>
                            <TextField id="outlined-basic" label="Name" variant="outlined" name='name' ref={register({required: true, pattern:[]})} fullWidth/>
                        </Grid>
                        <Grid item xs={12}>
                            <TextField id="outlined-basic" label="Email" variant="outlined" name='email' fullWidth/>
                        </Grid>
                        <Grid item xs={12}>
                            <TextField id="outlined-basic" label="Subject" variant="outlined" name='subject' fullWidth/>
                        </Grid>
                        <Grid item xs={12}>
                            <TextField id="outlined-basic" label="Message" variant="outlined" name='body' fullWidth multiline rows={3}/>
                        </Grid>
                        <Button type="submit" variant="contained"> Send</Button>
                    </Grid>
                </form>
            </Container>
        </div>
    )
}

export default withRouter(Contact)
