import React from 'react'
import { TextField, Container, Grid, Button } from '@material-ui/core';
import { withRouter } from 'react-router';
import { sendMessage } from '../../Data/Api/MessageApi';
import { useForm } from "react-hook-form";
import { useEffect } from 'react';

function Contact() {

    const { register, handleSubmit, watch, errors } = useForm();

    const onSubmit = async (e) => {


        // sendMessage(message);
    }

    useEffect(()=> {
        console.log(errors);
    }, [errors])

    return (
        <div>
            <Container maxWidth='sm' >
                <h1>Contact</h1>
                <form novalidate onSubmit={handleSubmit(onSubmit)}>
                    <Grid container spacing={2}>
                        <Grid item xs={12}>
                            <TextField id="outlined-basic" error={errors.name} label="Name" variant="outlined" name='name' ref={register({required: true, pattern:/[A-Za-z]/g})} fullWidth/>
                        </Grid>
                        <Grid item xs={12}>
                            <TextField id="outlined-basic" label="Email" variant="outlined" name='email' ref={register({required: true, email: true})} fullWidth/>
                        </Grid>
                        <Grid item xs={12}>
                            <TextField id="outlined-basic" label="Subject" variant="outlined" name='subject' ref={register({pattern:/[A-Za-z]/g})} fullWidth/>
                        </Grid>
                        <Grid item xs={12}>
                            <TextField id="outlined-basic" label="Message" variant="outlined" name='body' ref={register({required: true})}  fullWidth multiline rows={3}/>
                        </Grid>
                        <Button type="submit" variant="contained"> Send</Button>
                    </Grid>
                </form>
            </Container>
        </div>
    )
}

export default withRouter(Contact)
